import React, { Component } from 'react';
import {
    Button,
    Modal,
    ModalHeader,
    ModalBody,
    Form,
    FormGroup,
    Label,
    Input
} from 'reactstrap';

import { connect } from 'react-redux';
import { addPosts } from '../actions/postActions';

class PostModal extends Component {
    state = {
        modal: false,
        posttitle: '',
        postpost: ''
    }

    toggle = () => {
        this.setState({
            modal: !this.state.modal
        });
    }

    onChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    }

    render(){
        return (
            <div>
                <Button
                    color="dark"
                    style={{marginBottom: '2rem'}}
                    onClick={this.toggle}
                >
                    Add Post
                </Button>

                <Modal
                    isOpen={this.state.modal}
                    toggle={this.toggle}
                >
                    <ModalHeader toggle={this.toggle}>
                        Add To Posts
                    </ModalHeader>
                    <ModalBody>
                        <Form onSubmit={this.onSubmit}>
                           <FormGroup>

                           </FormGroup>
                        </Form>
                    </ModalBody>
                </Modal>
            </div>
        );
    }
}

export default connect()(PostModal);
