import React, { Component } from 'react';
import { Container, ListGroup, ListGroupItem, Button, ListGroupItemHeading, ListGroupItemText } from 'reactstrap';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import {v1 as uuid} from 'uuid';
import { connect } from 'react-redux';
import {getPosts} from '../actions/postActions';
import postReducer from '../reducers/postReducer';

class Posts extends Component {


    render(){
        const { posts } = this.state;
        return(
            <Container>
                <Button 
                    color="dark"
                    style={{ marginBottom: '2rem' }}
                    onClick={() => {
                        const title = prompt('Enter Title');
                        const post = prompt('Enter Post');
                        if (title && post){
                            this.setState(state => ({
                                posts: [...state.posts, {id: uuid(), title, post}]
                            }));
                        }
                    }}>
                    Add Post
                </Button>

                <ListGroup>
                    <TransitionGroup className="post-list">
                        {posts.map(({id, title, post}) => (
                            <CSSTransition key={id} timeout={500} classNames="fade">
                                <ListGroupItem>
                                    <Button
                                        className="remove-btn"
                                        color="danger"
                                        size="sm"
                                        onClick={() =>{
                                            this.setState(state =>({
                                                posts: state.posts.filter(post => post.id !== id)
                                            }));
                                        }}
                                    >

                                    </Button>
                                    <ListGroupItemHeading>{title}</ListGroupItemHeading>
                                    <ListGroupItemText>{post}</ListGroupItemText>
                                </ListGroupItem>
                            </CSSTransition>
                        ))}
                    </TransitionGroup>
                </ListGroup>
            </Container>
        );
    }
}

const mapStateToProps = (state) => ({
    post: postReducer
});

export default connect(mapStateToProps, { getPosts }) (Posts);
