import React, { Component } from 'react';
import { Container, ListGroup, ListGroupItem, Button, ListGroupItemHeading, ListGroupItemText } from 'reactstrap';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import { connect } from 'react-redux';
import {getPosts, deletePost} from '../actions/postActions';
import PropTypes from 'prop-types';

class Posts extends Component {

    componentDidMount(){
        this.props.getPosts();
    }

    onDeleteClick = (id) => {
        this.props.deletePost(id);
    };

    render(){
        const { posts } = this.props.post;
        return(
            <Container>
                <Button 
                    color="dark"
                    style={{ marginBottom: '2rem' }}
                    onClick={() => {
                        const title = prompt('Enter Title');
                        const post = prompt('Enter Post');
                        if (title && post){
                            this.setState(state => ({
                                posts: [...state.posts, {id: uuid(), title, post}]
                            }));
                        }
                    }}>
                    Add Post
                </Button>

                <ListGroup>
                    <TransitionGroup className="post-list">
                        {posts.map(({id, title, post}) => (
                            <CSSTransition key={id} timeout={500} classNames="fade">
                                <ListGroupItem>

                                    <ListGroupItemHeading>{title}</ListGroupItemHeading>
                                    <ListGroupItemText>{post}</ListGroupItemText>
                                </ListGroupItem>
                            </CSSTransition>
                        ))}
                    </TransitionGroup>
                </ListGroup>
            </Container>
        );
    }
}

Posts.propTypes = {
    getPosts: PropTypes.func.isRequired,
    post: PropTypes.object.isRequired
}

const mapStateToProps = (state) => ({
    post: state.post
});

export default connect(mapStateToProps, { getPosts, deletePost }) (Posts);
