import axios from 'axios';
import { GET_POSTS, ADD_POSTS, DELETE_POSTS, ITEMS_LOADING } from './types';

export const getPosts = () => dispatch => {
    dispatch(setItemsLoading());
    axios
        .get('/api/posts')
        .then(res => 
            dispatch({
                type: GET_POSTS,
                payload: res.data
            })
            )
};  


export const addPosts = (post) => dispatch => {
    axios
        .post('/api/posts', post)
        .then(res => 
            dispatch({
               type: ADD_POSTS,
               payload: res.data 
            })    
        )
}; 

export const deletePosts = (id) => dispatch => {
    axios.delete(`api/posts/${id}`).then(res => dispatch({
        type: DELETE_POSTS,
        payload: id
    })
    );
};


export const setItemsLoading = () => {
    return {
        type: ITEMS_LOADING
    };
};
