import React, { Component } from 'react';
import { Container, ListGroup, ListGroupItem, Button, ListGroupItemHeading, ListGroupItemText } from 'reactstrap';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import {v1 as uuid} from 'uuid';

class Posts extends Component {
    state = {
        posts: [
            {
                title: 'New School Update',
                post: 'School is starting next week',
                id: uuid()
            },
            {
                title: 'Graduation',
                post: 'This years graduation is on march',
                id: uuid()
            },
            {
                title: 'First years',
                post: 'Welcome to our university',
                id: uuid()
            }
        ]
    }

    render(){
        const { posts } = this.state;
        return(
            <Container>
                <Button 
                    color="dark"
                    style={{ marginBottom: '2rem' }}
                    onClick={() => {
                        const title = prompt('Enter Title');
                        const post = prompt('Enter Post');
                        if (title && post){
                            this.setState(state => ({
                                posts: [...state.posts, {id: uuid(), title, post}]
                            }));
                        }
                    }}>
                    Add Post
                </Button>

                <ListGroup>
                    <TransitionGroup className="post-list">
                        {posts.map(({id, title, post}) => (
                            <CSSTransition key={id} timeout={500} classNames="fade">
                                <ListGroupItem>
                                    <Button
                                        className="remove-btn"
                                        color="danger"
                                        size="sm"
                                        onClick
                                    >

                                    </Button>
                                    <ListGroupItemHeading>{title}</ListGroupItemHeading>
                                    <ListGroupItemText>{post}</ListGroupItemText>
                                </ListGroupItem>
                            </CSSTransition>
                        ))}
                    </TransitionGroup>
                </ListGroup>
            </Container>
        );
    }
}

export default Posts;
