import {v1 as uuid} from 'uuid';
import { GET_POSTS, ADD_POSTS, DELETE_POSTS } from '../actions/types';



const initialState = {
    posts: [
        {
            title: 'New School Update',
            post: 'School is starting next week',
            id: uuid()
        },
        {
            title: 'Graduation',
            post: 'This years graduation is on march',
            id: uuid()
        },
        {
            title: 'First years',
            post: 'Welcome to our university',
            id: uuid()
        }
    ]
}


export default function(state= initialState, action){
    switch(action.type){
        case GET_POSTS:
            return {
                ...state
            };
        case DELETE_POSTS:
            return {
                ...state,
                posts: state.posts.filter(post => post.id !== action.payload)
            }
        case ADD_POSTS:
            return {
                ...state,
            }
        default:
            return state;
    }
}
